"""
معالج الإحالات والهدايا
"""

import logging
from datetime import datetime, timedelta
from typing import List, Optional
from telegram import Update
from telegram.ext import ContextTypes
from telegram.error import TelegramError

from database import DatabaseManager, User, Transaction, Gift, GiftCode, GiftCodeUsage
from config import Config
from keyboards import Keyboards
from utils import format_currency, get_user_display_name, format_datetime, validate_gift_code

logger = logging.getLogger(__name__)
db = DatabaseManager()

class ReferralHandler:
    """معالج الإحالات"""
    
    @staticmethod
    async def show_referral_stats(update: Update, context: ContextTypes.DEFAULT_TYPE):
        """عرض إحصائيات الإحالات"""
        user = db.get_user(update.effective_user.id)
        
        # الحصول على قائمة المُحالين
        session = db.get_session()
        try:
            referred_users = session.query(User).filter(User.referred_by == user.referral_code).all()
            
            # حساب إجمالي الإيداعات من المُحالين
            total_referral_deposits = 0
            for referred_user in referred_users:
                deposits = session.query(Transaction).filter(
                    Transaction.user_id == referred_user.id,
                    Transaction.transaction_type == "deposit",
                    Transaction.status == "completed"
                ).all()
                total_referral_deposits += sum(t.amount for t in deposits)
            
            # إحصائيات مفصلة
            stats_message = f"""
📊 إحصائيات الإحالات التفصيلية

🔗 كود الإحالة: {user.referral_code}
👥 عدد الإحالات: {user.referral_count}
💰 أرباح الإحالات: {format_currency(user.referral_earnings)}
📈 نسبة الربح: {Config.REFERRAL_PERCENTAGE}%

💵 إجمالي إيداعات المُحالين: {format_currency(total_referral_deposits)}
💎 متوسط الربح لكل إحالة: {format_currency(user.referral_earnings / max(user.referral_count, 1))}

📋 قائمة المُحالين الأخيرة:
            """
            
            # عرض آخر 5 مُحالين
            recent_referrals = referred_users[-5:] if referred_users else []
            for referred_user in recent_referrals:
                stats_message += f"\n👤 {get_user_display_name(referred_user)} - {format_datetime(referred_user.created_at)}"
            
            if not recent_referrals:
                stats_message += "\n📭 لا توجد إحالات بعد"
            
            bot_username = context.bot.username
            referral_link = f"https://t.me/{bot_username}?start={user.referral_code}"
            
            stats_message += f"""

🔗 رابط الإحالة:
{referral_link}

💡 نصائح لزيادة الأرباح:
• شارك الرابط مع الأصدقاء والعائلة
• انشر في المجموعات والقنوات
• اشرح فوائد استخدام البوت
            """
            
        finally:
            session.close()
        
        if update.callback_query:
            await update.callback_query.edit_message_text(
                stats_message,
                reply_markup=Keyboards.referral_menu()
            )
        else:
            await update.message.reply_text(
                stats_message,
                reply_markup=Keyboards.referral_menu()
            )
    
